      const updateData = await subcategoryModel.updateOne(
        { _id: subcategory_id },
        {
         $set: {
            title: title,
            email: email,
            password: password,
            contact: contact,
             image:img
          },
        })
        if(updateData.acknowledged){
          return res.status(200).json({
              message: "Updated"
          })
        }
       })
  
       
      
